local function flashOnce(label, interval) local t1 = TweenService:Create(label, TweenInfo.new(interval/2, Enum.EasingStyle.Linear), {TextTransparency = 1}) local t2 = TweenService:Create(label, TweenInfo.new(interval/2, Enum.EasingStyle.Linear), {TextTransparency = 0}) t1:Play(); t1.Completed:Wait() t2:Play(); t2.Completed:Wait() end
local function animatePhase(label, baseText, duration) local t0, dots = tick(), 0 while tick() - t0 < duration do dots = (dots % 3) + 1 label.Text = baseText .. string.rep(".", dots) flashOnce(label, 0.6) end end
